mapper.select:
SELECT id, name, city_id, address, latitude, longitude, food_type, avg_price, intro, image_url, score, hot_score, is_recommend, create_time, update_time, del_flag FROM food WHERE del_flag = 0
AND city_id = #{cityId}
AND is_recommend = #{isRecommend}
AND name LIKE CONCAT('%', #{name}, '%')
ORDER BY hot_score DESC, score DESC

SELECT f.id, f.name, f.city_id, f.address, f.latitude, f.longitude, f.food_type, f.avg_price, f.intro, f.image_url, f.score, f.hot_score, f.is_recommend, f.create_time, f.update_time, f.del_flag FROM food f LEFT JOIN city c ON f.city_id = c.id WHERE f.del_flag = 0
AND f.city_id = #{query.cityId}
AND f.is_recommend = #{query.isRecommend}
AND f.name LIKE CONCAT('%', #{query.name}, '%')
AND f.food_type LIKE CONCAT('%', #{query.foodType}, '%')
AND ( c.province = #{query.province} OR c.province = CONCAT(#{query.province}, '市') OR c.province = CONCAT(#{query.province}, '省') OR c.province = CONCAT(#{query.province}, '自治区') OR c.province = CONCAT(#{query.province}, '特别行政区') OR c.province LIKE CONCAT(#{query.province}, '%') OR #{query.province} LIKE CONCAT(c.province, '%') OR REPLACE(REPLACE(REPLACE(REPLACE(c.province, '市', ''), '省', ''), '自治区', ''), '特别行政区', '') = REPLACE(REPLACE(REPLACE(REPLACE(#{query.province}, '市', ''), '省', ''), '自治区', ''), '特别行政区', '') )
AND ( c.city_name = #{query.city} OR c.city_name = CONCAT(#{query.city}, '市') OR c.city_name LIKE CONCAT('%', #{query.city}, '%') OR #{query.city} LIKE CONCAT('%', c.city_name, '%') OR REPLACE(c.city_name, '市', '') = REPLACE(#{query.city}, '市', '') )
ORDER BY f.hot_score DESC, f.score DESC LIMIT #{offset}, #{pageSize}

SELECT id, name, city_id, address, latitude, longitude, food_type, avg_price, intro, image_url, score, hot_score, is_recommend, create_time, update_time, del_flag FROM food WHERE del_flag = 0
AND city_id = #{cityId}
ORDER BY hot_score DESC, score DESC LIMIT #{limit}

SELECT f.id, f.name, f.city_id, f.address, f.latitude, f.longitude, f.food_type, f.avg_price, f.intro, f.image_url, f.score, f.hot_score, f.is_recommend, f.create_time, f.update_time, f.del_flag FROM food f INNER JOIN city c ON f.city_id = c.id WHERE f.del_flag = 0 AND c.del_flag = 0
AND ( c.province = #{province} OR c.province = CONCAT(#{province}, '市') OR c.province = CONCAT(#{province}, '省') OR c.province = CONCAT(#{province}, '自治区') OR c.province = CONCAT(#{province}, '特别行政区') OR c.province LIKE CONCAT(#{province}, '%') OR #{province} LIKE CONCAT(c.province, '%') OR REPLACE(REPLACE(REPLACE(REPLACE(c.province, '市', ''), '省', ''), '自治区', ''), '特别行政区', '') = REPLACE(REPLACE(REPLACE(REPLACE(#{province}, '市', ''), '省', ''), '自治区', ''), '特别行政区', '') OR REPLACE(REPLACE(REPLACE(c.province, '市', ''), '省', ''), '自治区', '') = #{province} OR REPLACE(REPLACE(REPLACE(#{province}, '市', ''), '省', ''), '自治区', '') = REPLACE(REPLACE(REPLACE(c.province, '市', ''), '省', ''), '自治区', '') )
ORDER BY (f.hot_score * 2 + COALESCE(f.score, 0)) DESC LIMIT #{limit}

mapper.update:
UPDATE food
name = #{name}, city_id = #{cityId}, address = #{address}, latitude = #{latitude}, longitude = #{longitude}, food_type = #{foodType}, avg_price = #{avgPrice}, intro = #{intro}, image_url = #{imageUrl}, score = #{score}, hot_score = #{hotScore}, is_recommend = #{isRecommend}, del_flag = #{delFlag}, update_time = NOW()
WHERE id = #{id}
