mapper.update:
UPDATE checkin_point
name = #{name}, target_type = #{targetType}, target_id = #{targetId}, target_name = #{targetName}, image_url = #{imageUrl}, location = #{location}, checkin_count = #{checkinCount}, is_active = #{isActive}, update_by = #{updateBy}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT id, name, target_type, target_id, target_name, image_url, location, checkin_count, is_active,
       create_time, update_time, create_by, update_by, del_flag
FROM checkin_point

    AND del_flag = #{query.delFlag}
    AND name LIKE CONCAT('%', #{query.name}, '%')
    AND target_type = #{query.targetType}
    AND is_active = #{query.isActive}

ORDER BY create_time DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(1)
FROM checkin_point

    AND del_flag = #{query.delFlag}
    AND name LIKE CONCAT('%', #{query.name}, '%')
    AND target_type = #{query.targetType}
    AND is_active = #{query.isActive}
